mapper.select:
SELECT newsNo
FROM NEWS
WHERE originLink = #{originLink}

SELECT nr.replyNo, content, regDate, newsNo, nr.memberNo, nickName, NVL(likeCount,0) likeCount, NVL(userReplyLiked,0) userReplyLiked, nr.showHide FROM newsReply nr JOIN member m ON m.memberNo = nr.memberNo LEFT OUTER JOIN ( SELECT replyNo, COUNT(*) likeCount FROM newsReplyLike GROUP BY replyNo ) nrl ON nr.replyNo = nrl.replyNo LEFT OUTER JOIN ( SELECT replyNo, COUNT(*) userReplyLiked FROM newsReplyLike nrlRe JOIN member m ON m.memberNo = nrlRe.memberNo WHERE m.memberNo = #{memberNo} GROUP BY replyNo ) nrl2 ON nr.replyNo = nrl2.replyNo WHERE newsNo = #{newsNo}
AND (showHide=0 OR (showHide=-1 AND m.memberNo = #{memberNo}))
ORDER BY replyNo OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

SELECT newsNo, memberNo 
FROM newsLike 
WHERE newsNo = #{newsNo} AND memberNo = #{memberNo}

SELECT COUNT(*)
FROM newsLike
WHERE newsNo = #{newsNo}

SELECT COUNT(*)
FROM complain
WHERE replyNo = #{replyNo}

SELECT replyNo, memberNo
FROM complain
WHERE replyNo = #{replyNo} AND memberNo = #{memberNo}

SELECT COUNT(*)
FROM newsReplyLike
WHERE replyNo = #{replyNo}

SELECT COUNT(*)
FROM newsReplyLike
WHERE replyNo = #{replyNo} AND memberNo = #{memberNo}
